$(document).ready(function(){$('#userip').click(function(){$('#host').val($('#userip').text())});$('#networktest').submit(function(){var host=$('input[name=host]').val();var data='cmd='+$('select[name=cmd]').val()+'&host='+host;if(host==''){$('#hosterror').addClass('has-error')}else{$('#submit').attr('disabled','true').text(Loading);$('#response').html();var xhr=new XMLHttpRequest();xhr.open('GET','ajax.php?'+data,true);xhr.send(null);var timer;timer=window.setInterval(function(){if(xhr.readyState==XMLHttpRequest.DONE){window.clearTimeout(timer);$('#submit').removeAttr('disabled').text(RunTest)}if(xhr.responseText==''){$('#response').hide()}else{$('#hosterror').removeClass('has-error');$('#response, #results').show()}if(xhr.responseText=='Unauthorized request'){$('#results').hide();$('#hosterror').addClass('has-error')}else{$('#response').html(xhr.responseText.replace(/<br \/> +/g,'<br />'))}},500)}return false})});